function Cart(){

    const [localdata,setlocaldata] = useState([])
    const [items,setitems] = useState(null)
    const [totalprice,settotalprice] = useState(null)
    useEffect(()=>{

var existing = JSON.parse(localStorage.getItem("cartdata"));
console.log(existing)
var mapfirst = existing.map(e => e[0] || e);
setlocaldata(mapfirst)

setitems(existing.length)
setlocaldata(mapfirst)
    },[])
    console.log(localdata,"kl")

    return(
        <>
        <Nav/>
<h1>.</h1>

        <div className="bigConCart">
            <div className="cartleft">
        {localdata && localdata.map(e =>(
            <>
            <div className="conOfCart">
                <img className="cartData" src={`/${e.image}`}></img>
            <p className="cartData">{e.pname}</p> 
            <p className="cartData">{e.price} <span>ks</span></p>
            </div>



            </>
        ))}</div>

  <div className="cartright">
    <p>Total items = {items}</p>
    <p>Total price= {totalprice}<span>ks</span></p>
    <p>Discount = 10%</p>
    <p className="total">Total = 1900<span>ks</span></p>
    <p className="buynow">Buy Now</p>

  </div>

        </div>
        <h1>this is cart page</h1>
        <Footer/>
        </>
    )
}
export default Cart;
