import { Link } from "react-router-dom"
import Cart from "./Cart"
import "./nav.css"
import { useEffect, useState } from "react";
import { FaBeer,FaBars} from "react-icons/fa";
import {IconContext} from "react-icons"
function Nav(props){
    const [menu,setmenu] = useState(false)
const [no,setno] = useState("")
useEffect(()=>{
const localno = localStorage.getItem('cartdata');

  if(localno){const a = JSON.parse(localno);

     setno(a.length)}


},[])
function menuclick(){
  console.log("hi")

  if(menu){
    setmenu(false);

  }
  else{
    setmenu(true);
    body.style.overflow = hidden;
  }
}
return(
    <>
     <div className="nav"> 

     <p className="logo">HONEY BOBA</p>





     <Link  to ={{pathname:"/cart"}}><div className="cartcon"><div className="cartno">{no || ""}</div><img className="cartimg" src="/cart.jpg"></img></div></Link>

     <FaBars onClick={menuclick} className="r"/>


     </div>
     {menu && 
      <div className="menucon">
      <p className="menudata">HOME</p>
      <p className="menudata">CONTACT US</p>
      <p className="menudata">ABOUT</p>
      <p className="menudata">HISTORY</p>

     </div>}

    </>
)
}
export default Nav;
