          settotalprice(newtotal)
        }
        setlocaldata(updatedLocalData);
    console.log(index,"inidex")



    }
    function plus(index){
        return () => {

            const updatedLocalData = [...localdata];
            updatedLocalData[index].quantity++;
            setlocaldata(updatedLocalData);

            const newtotal = parseInt(updatedLocalData[index].price) + totalprice
          settotalprice(newtotal)
            console.log(newtotal,"muu")

        }
    }

const productid =[{1:2 },{9:3}];
    const [name,setname] = useState("");
const [phone,setphone] = useState("");
const [address,setaddress] = useState("");
    function namechange(e){
        setname(e.target.value)
    }

    function phonechange(e){
        setphone(e.target.value)
    }
    function addresschange(e){
        setaddress(e.target.value)
    }
    function buysubmit(){
    console.log(name,phone,address,productid,"this is data")




        async function fe() {
            await axios.post("http://localhost:4500/buyer", {
                data: JSON.stringify({name:name,phone:phone,address:address,productid:productid}),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
                .then(resp => {

                    console.log(resp.data, "axios this is a data")



                }).catch(
                    error => console.log(error)
                )
        }
        fe()
    }

function buynow(){
    setbu(true)
}
let i = [];
 localdata.map(e =>(i += `${e.id},`))
console.log(i,"everyfd")
