                    <p className="cartrighttext">Discount = 10%</p>
                    <p className="totalprice">Total price = {totalprice}<span>ks</span></p>
                    <p   onClick={buynow} className="buynow">Buy Now</p>

                </div>

            </div>

            <Footer  />
        </>
